CREATE DATABASE iagora;
USE iagora

CREATE TABLE pergunta (
    id INT AUTO_INCREMENT PRIMARY KEY,
    pergunta TEXT NOT NULL,
    nicho VARCHAR(50) NOT NULL,
    resp_chatgpt TEXT NOT NULL,
    resp_deepseak TEXT NOT NULL,
    resp_gemini TEXT NOT NULL,
    resp_1 TEXT NOT NULL,
    resp_2 TEXT NOT NULL,
    resp_3 TEXT NOT NULL
);

CREATE TABLE entrevistado (
    id INT AUTO_INCREMENT PRIMARY KEY,
    nome VARCHAR(255) NOT NULL,
    idade INT NOT NULL,
    sexo ENUM('m', 'f', 'n') NOT NULL,
    data_criacao DATETIME DEFAULT CURRENT_TIMESTAMP,
    status TINYINT NOT NULL,
    opiniao TEXT NOT NULL,
    resposta TEXT NOT NULL,
    nome_ia varchar(8) NOT NULL,
    id_pergunta INT NOT NULL,
    FOREIGN KEY (id_pergunta) REFERENCES pergunta(id)
);

